package com.bihe0832.readhub.libware.util;import java.util.ArrayList;public class Math {    /**     * 获取两个数字区间的随机数     *     * @param min     *            下限     * @param max     *            上限     * @return 最终数     */    public static int getRandNumByLimit(int min, int max) {        return (int) java.lang.Math.round(java.lang.Math.random() * (max - min) + min);    }    /**     * 返回多次随机数的结果     * @param min     * @param max     * @param num     * @return     */    public static ArrayList<Integer> getRandNumByLimitAndNum(int min, int max, int num) {        ArrayList<Integer> tempList = new ArrayList<Integer>();        for (int i = 0; i < num; i++) {            int result = Math.getRandNumByLimit(min, max);            tempList.add(result);        }        return tempList;    }}